<script>
    // Mở hoặc đóng danh sách thông báo khi nhấn vào icon
    const notificationIcon = document.getElementById('notification-icon');
    const listNoti = document.querySelector('.list-noti');

    notificationIcon.addEventListener('click', function(event) {
        event.stopPropagation(); // Ngừng sự kiện lan truyền để không bị đóng khi nhấn vào icon
        listNoti.classList.toggle('show');
    });

    // Đóng danh sách khi nhấn vào ngoài
    document.addEventListener('click', function(event) {
        if (!notificationIcon.contains(event.target) && !listNoti.contains(event.target)) {
            listNoti.classList.remove('show');
        }
    });

    $(document).ready(function() {
        // Mark a single notification as read
        $('.mark-as-read').on('click', function() {
            const id = $(this).data('id'); // Lấy ID thông báo
            $.ajax({
                url: `/notifications/${id}/mark-as-read`,
                type: 'PATCH',
                headers: {
                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content'),
                },
                success: function(response) {
                    if (response.success) {
                        // Cập nhật giao diện thông báo
                        const notificationItem = $(`.notification-item[data-id="${id}"]`);
                        notificationItem.removeClass('bg-light');
                        notificationItem.find('.mark-as-read').remove();

                        // Gọi hàm cập nhật số lượng
                        updateNotificationCount();
                    }
                },
                error: function(xhr) {
                    console.error('Error marking notification as read:', xhr.responseText);
                },
            });
        });
        // Mark all notifications as read
        $('.markAllRead').on('click', function() {
            const type = $(this).data('type');
            const ids = $(this).data('ids');

            $.ajax({
                url: `/notifications/mark-all-as-read/${type}`,
                type: 'PATCH',
                headers: {
                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content'),
                },
                data: {
                    ids: ids
                },
                success: function(response) {
                    if (response.success) {
                        // Cập nhật giao diện thông báo
                        $('.notification-item.bg-light').each(function() {
                            $(this).removeClass('bg-light');
                            $(this).find('.mark-as-read').remove();
                        });

                        // Gọi hàm cập nhật số lượng
                        updateNotificationCount();
                    }
                },
                error: function(xhr) {
                    console.error('Error marking all notifications as read:', xhr
                        .responseText);
                },
            });
        });

        function updateNotificationCount() {
            $.ajax({
                url: '/notifications/count',
                method: 'GET',
                success: function(data) {
                    // Cập nhật số lượng vào phần tử hiển thị
                    $('#notification-count').text(data.count);

                    // Ẩn nếu không còn thông báo chưa đọc
                    if (data.count === 0) {
                        $('#notification-count').hide();
                    } else {
                        $('#notification-count').show();
                    }
                },
                error: function(xhr) {
                    console.error('Error updating notification count:', xhr.responseText);
                },
            });
        }
    });
</script>
